#pragma config(Sensor, dgtl1,  Up,             sensorTouch)
#pragma config(Sensor, dgtl2,  Down,           sensorTouch)
#pragma config(Sensor, dgtl3,  ShootTrigger,   sensorTouch)
#pragma config(Sensor, dgtl4,  PivotEncoder,   sensorQuadEncoder)
#pragma config(Sensor, dgtl12, Indicate,       sensorLEDtoVCC)
#pragma config(Motor,  port1,           Pivot,         tmotorVex393_HBridge, openLoop)
#pragma config(Motor,  port2,           Shoot,         tmotorVex393_MC29, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

const int Setpoint1 = 10;
const int Setpoint2 = 20;
const int Setpoint3 = 30;
const int Setpoint4 = 40;
const int Setpoint5 = 50;
const int Setpoint6 = 60;
const int Setpoint7 = 70;
const int Setpoint8 = 80;
int SetpointSelector = 1;
int TargetPose = 10;




task main() {
while (true){
if(SetpointSelector == 1){TargetPose=Setpoint1;}
if(SetpointSelector == 2){TargetPose=Setpoint2;}
if(SetpointSelector == 3){TargetPose=Setpoint3;}
if(SetpointSelector == 4){TargetPose=Setpoint4;}
if(SetpointSelector == 5){TargetPose=Setpoint5;}
if(SetpointSelector == 6){TargetPose=Setpoint6;}
if(SetpointSelector == 7){TargetPose=Setpoint7;}
if(SetpointSelector == 8){TargetPose=Setpoint8;}
//if (SensorValue(PivotEncoder) - CurrentSetpointValue){}

if(SensorValue(ShootTrigger)){
	startMotor(Shoot, 127);}
if(SensorValue(ShootTrigger)==0){
	stopMotor(Shoot);}

if(SensorValue(Up)){
	if(SetpointSelector<=7){
		SetpointSelector = SetpointSelector +1;}
while (SensorValue(Up)){
wait(.01);}}

if(SensorValue(Down)){
	if(SetpointSelector>=2){
		SetpointSelector = SetpointSelector -1;}
while (SensorValue(Down)){
wait(.01);}}

}
}
